"use strict";(()=>{var a=class{DATA_ATTR="data-dialog-id";DATA_ATTR_OPEN="data-dialog-open";DATA_ATTR_CLOSE="data-dialog-close";DATA_COMPONENT_SELECTOR=`dialog[${this.DATA_ATTR}]`;initializedIds=new Set;constructor(){this.init(),this.handleBackdropClick()}init(){document.querySelectorAll(this.DATA_COMPONENT_SELECTOR).forEach(i=>{let t=i.getAttribute(this.DATA_ATTR);if(!t){console.error("No ID found for dialog component",i);return}if(this.initializedIds.has(t)){console.warn(`Duplicate dialog ID "${t}" found. Skipping initialization.`,i);return}this.initializedIds.add(t);let l=document.querySelectorAll(`[${this.DATA_ATTR_OPEN}="${t}"]`),o=i.querySelectorAll(`[${this.DATA_ATTR_CLOSE}="${t}"]`);l.forEach(n=>{n.addEventListener("click",()=>{this.openDialog(i)})}),o.forEach(n=>{n.addEventListener("click",()=>{this.closeDialog(i)})})})}openDialog(e){e.showModal();let i=new CustomEvent("dialogOpen",{detail:{dialogId:e.getAttribute(this.DATA_ATTR)}});e.dispatchEvent(i)}closeDialog(e){e.close();let i=new CustomEvent("dialogClose",{detail:{dialogId:e.getAttribute(this.DATA_ATTR)}});e.dispatchEvent(i)}handleBackdropClick(){document.querySelectorAll("dialog").forEach(i=>{i.addEventListener("click",t=>{let l=t.target;if(!(l instanceof HTMLDialogElement))return;let o=l.getBoundingClientRect();o.top<=t.clientY&&t.clientY<=o.top+o.height&&o.left<=t.clientX&&t.clientX<=o.left+o.width&&t.target===l&&this.closeDialog(l)})})}},c=a;})();
